func (self *Account) InitVerifier(c *client.Client, to *Account, value *big.Int, humanReadable bool) (common.Address, *types.Transaction, *big.Int, error) {
	ctx := context.Background() //context.WithTimeout(context.Background(), 100*time.Second)

	self.mutex.Lock()
	defer self.mutex.Unlock()

	nonce := self.GetNonce(c)

	gaslimit := uint64(10000000)
	if humanReadable {
		gaslimit = uint64(4100000000)
	}

	contractABI := verifierABI
	parsed, err := abi.JSON(strings.NewReader(contractABI))

	// Init - register pair
	ke := sha3.NewKeccak256()

	ke.Write([]byte("BridgeERC20TransferRequest(address,address,uint256,address,address)"))
	buf := ke.Sum(nil)
	var arr [32]byte
	copy(arr[:], buf)

	data, err := parsed.Pack("registerTokenPair", SmartContractAddr, SmartContractAddr, arr)
	if err != nil {
		log.Fatalf("failed to abi.Pack: %v", err)
	}

	signer := types.NewEIP155Signer(chainID)
	tx, err := types.NewTransactionWithMap(types.TxTypeSmartContractExecution, map[types.TxValueKeyType]interface{}{
		types.TxValueKeyNonce:    nonce,
		types.TxValueKeyGasPrice: gasPrice,
		types.TxValueKeyGasLimit: gaslimit,
		types.TxValueKeyFrom:     self.address,
		types.TxValueKeyAmount:   value,
		types.TxValueKeyTo:       VerifierContractAddr,
		types.TxValueKeyData:     data,
	})
	if err != nil {
		log.Fatalf("Failed to encode tx: %v", err)
	}

	err = tx.SignWithKeys(signer, self.privateKey)
	if err != nil {
		log.Fatalf("Failed to sign tx: %v", err)
	}

	hash, err := c.SendRawTransaction(ctx, tx)
	if err != nil {
		if err.Error() == blockchain.ErrNonceTooLow.Error() || err.Error() == blockchain.ErrReplaceUnderpriced.Error() {
			fmt.Printf("registerTokenPair Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
			fmt.Printf("registerTokenPair Account(%v) nonce is added to %v\n", self.GetAddress().String(), nonce+1)
			self.nonce++
		} else {
			fmt.Printf("registerTokenPair Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
		}
		return common.Address{}, nil, nil, err
	}
	fmt.Printf("Verifier register pair hash: 0x%v\n", hex.EncodeToString(hash[:]))

	self.nonce++
	nonce++

	// register verifier

	data, err = parsed.Pack("registerVerifier", VerifierContractAddr)
	if err != nil {
		log.Fatalf("failed to abi.Pack: %v", err)
	}

	signer = types.NewEIP155Signer(chainID)
	tx, err = types.NewTransactionWithMap(types.TxTypeSmartContractExecution, map[types.TxValueKeyType]interface{}{
		types.TxValueKeyNonce:    nonce,
		types.TxValueKeyGasPrice: gasPrice,
		types.TxValueKeyGasLimit: gaslimit,
		types.TxValueKeyFrom:     self.address,
		types.TxValueKeyAmount:   value,
		types.TxValueKeyTo:       VerifierContractAddr,
		types.TxValueKeyData:     data,
	})
	if err != nil {
		log.Fatalf("Failed to encode tx: %v", err)
	}

	err = tx.SignWithKeys(signer, self.privateKey)
	if err != nil {
		log.Fatalf("Failed to sign tx: %v", err)
	}

	hash, err = c.SendRawTransaction(ctx, tx)
	if err != nil {
		if err.Error() == blockchain.ErrNonceTooLow.Error() || err.Error() == blockchain.ErrReplaceUnderpriced.Error() {
			fmt.Printf("registerVerifier Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
			fmt.Printf("registerVerifier Account(%v) nonce is added to %v\n", self.GetAddress().String(), nonce+1)
			self.nonce++
		} else {
			fmt.Printf("registerVerifier Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
		}
		return common.Address{}, nil, nil, err
	}
	fmt.Printf("Verifier registerVerifier hash: 0x%v\n", hex.EncodeToString(hash[:]))

	self.nonce++
	nonce++

	// set fee receiver
	data, err = parsed.Pack("setFeeReceiver", self.address)
	if err != nil {
		log.Fatalf("failed to abi.Pack: %v", err)
	}

	signer = types.NewEIP155Signer(chainID)
	tx, err = types.NewTransactionWithMap(types.TxTypeSmartContractExecution, map[types.TxValueKeyType]interface{}{
		types.TxValueKeyNonce:    nonce,
		types.TxValueKeyGasPrice: gasPrice,
		types.TxValueKeyGasLimit: gaslimit,
		types.TxValueKeyFrom:     self.address,
		types.TxValueKeyAmount:   value,
		types.TxValueKeyTo:       VerifierContractAddr,
		types.TxValueKeyData:     data,
	})
	if err != nil {
		log.Fatalf("Failed to encode tx: %v", err)
	}

	err = tx.SignWithKeys(signer, self.privateKey)
	if err != nil {
		log.Fatalf("Failed to sign tx: %v", err)
	}

	hash, err = c.SendRawTransaction(ctx, tx)
	if err != nil {
		if err.Error() == blockchain.ErrNonceTooLow.Error() || err.Error() == blockchain.ErrReplaceUnderpriced.Error() {
			fmt.Printf("setFeeReceiver Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
			fmt.Printf("setFeeReceiver Account(%v) nonce is added to %v\n", self.GetAddress().String(), nonce+1)
			self.nonce++
		} else {
			fmt.Printf("setFeeReceiver Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
		}
		return common.Address{}, nil, nil, err
	}
	fmt.Printf("Verifier setFeeReceiver hash: 0x%v\n", hex.EncodeToString(hash[:]))

	self.nonce++
	nonce++

	// register fee sched
	bridgeFee := &big.Int{}
	hdFee := &big.Int{}
	govFee := &big.Int{}
	bridgeFee.SetUint64(uint64(2000000000000000000))
	hdFee.SetUint64(uint64(3000000000000000000))
	govFee.SetUint64(uint64(4000000000000000000))
	data, err = parsed.Pack("registerFeeSchedule", common.Big0, bridgeFee, hdFee, govFee)
	if err != nil {
		log.Fatalf("failed to abi.Pack: %v", err)
	}

	signer = types.NewEIP155Signer(chainID)
	tx, err = types.NewTransactionWithMap(types.TxTypeSmartContractExecution, map[types.TxValueKeyType]interface{}{
		types.TxValueKeyNonce:    nonce,
		types.TxValueKeyGasPrice: gasPrice,
		types.TxValueKeyGasLimit: gaslimit,
		types.TxValueKeyFrom:     self.address,
		types.TxValueKeyAmount:   value,
		types.TxValueKeyTo:       VerifierContractAddr,
		types.TxValueKeyData:     data,
	})
	if err != nil {
		log.Fatalf("Failed to encode tx: %v", err)
	}

	err = tx.SignWithKeys(signer, self.privateKey)
	if err != nil {
		log.Fatalf("Failed to sign tx: %v", err)
	}

	hash, err = c.SendRawTransaction(ctx, tx)
	if err != nil {
		if err.Error() == blockchain.ErrNonceTooLow.Error() || err.Error() == blockchain.ErrReplaceUnderpriced.Error() {
			fmt.Printf("registerFeeSchedule Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
			fmt.Printf("registerFeeSchedule Account(%v) nonce is added to %v\n", self.GetAddress().String(), nonce+1)
			self.nonce++
		} else {
			fmt.Printf("registerFeeSchedule Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
		}
		return common.Address{}, nil, nil, err
	}
	fmt.Printf("Verifier registerFeeSched hash: 0x%v\n", hex.EncodeToString(hash[:]))

	self.nonce++
	nonce++

	// approve
	contractABI = erc20PerformanceABI
	parsed, err = abi.JSON(strings.NewReader(contractABI))

	amount := &big.Int{}
	amount.SetInt64(100)
	data, err = parsed.Pack("approve", VerifierContractAddr, amount)
	if err != nil {
		log.Fatalf("failed to abi.Pack: %v", err)
	}

	signer = types.NewEIP155Signer(chainID)
	tx, err = types.NewTransactionWithMap(types.TxTypeSmartContractExecution, map[types.TxValueKeyType]interface{}{
		types.TxValueKeyNonce:    nonce,
		types.TxValueKeyGasPrice: gasPrice,
		types.TxValueKeyGasLimit: gaslimit,
		types.TxValueKeyFrom:     self.address,
		types.TxValueKeyAmount:   value,
		types.TxValueKeyTo:       SmartContractAddr,
		types.TxValueKeyData:     data,
	})
	if err != nil {
		log.Fatalf("Failed to encode tx: %v", err)
	}

	err = tx.SignWithKeys(signer, self.privateKey)
	if err != nil {
		log.Fatalf("Failed to sign tx: %v", err)
	}

	hash, err = c.SendRawTransaction(ctx, tx)
	if err != nil {
		if err.Error() == blockchain.ErrNonceTooLow.Error() || err.Error() == blockchain.ErrReplaceUnderpriced.Error() {
			fmt.Printf("approve Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
			fmt.Printf("approve Account(%v) nonce is added to %v\n", self.GetAddress().String(), nonce+1)
			self.nonce++
		} else {
			fmt.Printf("approve Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
		}
		return common.Address{}, nil, nil, err
	}
	fmt.Printf("Verifier approve hash: 0x%v\n", hex.EncodeToString(hash[:]))

	self.nonce++
	nonce++

	return common.Address{}, tx, nil, nil
}

func (self *Account) DeployGovParam(c *client.Client, to *Account, value *big.Int, humanReadable bool) (common.Address, *types.Transaction, *big.Int, error) {
	ctx := context.Background() //context.WithTimeout(context.Background(), 100*time.Second)

	self.mutex.Lock()
	defer self.mutex.Unlock()

	nonce := self.GetNonce(c)

	gaslimit := uint64(10000000)
	if humanReadable {
		gaslimit = uint64(4100000000)
	}

	contractABI := govParamABI
	parsed, err := abi.JSON(strings.NewReader(contractABI))
	byteCode := govParamByteCode

	if err != nil {
		fmt.Println("Error while parsing contractABI", "err", err)
	}
	txOpts := &bind.TransactOpts{
		From: self.address, Nonce: big.NewInt(int64(nonce)),
		Signer: func(signer types.Signer, address common.Address, tx *types.Transaction) (*types.Transaction, error) {
			if address != self.address {
				return nil, errors.New("not authorized to sign this account")
			}
			return types.SignTx(tx, signer, self.privateKey[0])
		}, Value: common.Big0,
		GasPrice: gasPrice, GasLimit: gaslimit, Context: ctx}
	contractAddr, contractTx, _, err := bind.DeployContract(txOpts, parsed, byteCode, c)
	if err != nil {
		log.Fatalf("Failed to DeployContract tx: %v", err)
	}

	self.nonce++
	GovParamContractAddr = contractAddr
	return contractAddr, contractTx, gasPrice, nil
}

func (self *Account) DeployERC20(c *client.Client, to *Account, value *big.Int, humanReadable bool) (common.Address, *types.Transaction, *big.Int, error) {
	ctx := context.Background() //context.WithTimeout(context.Background(), 100*time.Second)

	self.mutex.Lock()
	defer self.mutex.Unlock()

	nonce := self.GetNonce(c)
	/*if nonce != 0 {
		fmt.Println("Contract seems to already have been deployed!", "nonce", nonce)
		return common.Address{}, nil, nil, AlreadyDeployedErr
	}*/

	gaslimit := uint64(10000000)
	if humanReadable {
		gaslimit = uint64(4100000000)
	}

	contractABI := erc20PerformanceABI
	parsed, err := abi.JSON(strings.NewReader(contractABI))
	byteCode := erc20PerformanceByteCode

	if err != nil {
		fmt.Println("Error while parsing contractABI", "err", err)
	}
	txOpts := &bind.TransactOpts{
		From: self.address, Nonce: big.NewInt(int64(nonce)),
		Signer: func(signer types.Signer, address common.Address, tx *types.Transaction) (*types.Transaction, error) {
			if address != self.address {
				return nil, errors.New("not authorized to sign this account")
			}
			return types.SignTx(tx, signer, self.privateKey[0])
		}, Value: common.Big0,
		GasPrice: gasPrice, GasLimit: gaslimit, Context: ctx}
	contractAddr, contractTx, _, err := bind.DeployContract(txOpts, parsed, byteCode, c)
	SmartContractAddr = contractAddr
	if err != nil {
		log.Fatalf("Failed to DeployContract tx: %v", err)
	}

	self.nonce++
	return contractAddr, contractTx, gasPrice, nil
}

func (self *Account) TransferERC20(initialCharge bool, c *client.Client, tokenContractAddr common.Address, tokenRecipient *Account, value *big.Int) (*types.Transaction, *big.Int, error) {
	ctx := context.Background() //context.WithTimeout(context.Background(), 100*time.Second)

	self.mutex.Lock()
	defer self.mutex.Unlock()

	var nonce uint64
	if initialCharge {
		nonce = self.GetNonceFromBlock(c)
	} else {
		nonce = self.GetNonce(c)
	}

	abiStr := erc20PerformanceABI

	abii, err := abi.JSON(strings.NewReader(abiStr))
	if err != nil {
		log.Fatalf("failed to abi.JSON: %v", err)
	}
	data, err := abii.Pack("transfer", tokenRecipient.address, value)
	if err != nil {
		log.Fatalf("failed to abi.Pack: %v", err)
	}

	signer := types.NewEIP155Signer(chainID)
	tx, err := types.NewTransactionWithMap(types.TxTypeSmartContractExecution, map[types.TxValueKeyType]interface{}{
		types.TxValueKeyNonce:    nonce,
		types.TxValueKeyGasPrice: gasPrice,
		types.TxValueKeyGasLimit: uint64(5000000),
		types.TxValueKeyFrom:     self.address,
		types.TxValueKeyAmount:   big.NewInt(0),
		types.TxValueKeyTo:       tokenContractAddr,
		types.TxValueKeyData:     data,
	})
	if err != nil {
		log.Fatalf("Failed to encode tx: %v", err)
	}

	log.Println("tokenContractAddr", tokenContractAddr.String(), "tokenRecipientAddr", tokenRecipient.address.String())

	err = tx.SignWithKeys(signer, self.privateKey)
	if err != nil {
		log.Fatalf("Failed to sign tx: %v", err)
	}

	_, err = c.SendRawTransaction(ctx, tx)
	if err != nil {
		if err.Error() == blockchain.ErrNonceTooLow.Error() || err.Error() == blockchain.ErrReplaceUnderpriced.Error() {
			fmt.Printf("Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
			fmt.Printf("Account(%v) nonce is added to %v\n", self.GetAddress().String(), nonce+1)
			self.nonce++
		} else {
			fmt.Printf("Account(%v) nonce(%v) : Failed to sendTransaction: %v\n", self.GetAddress().String(), nonce, err)
		}
		return tx, gasPrice, err
	}

	self.nonce++

	return tx, gasPrice, nil
}
